% ns=i;
% Initialize arrays to store fits and goodness-of-fit.
fontsize=15;
fitresult = cell( 4, 1 );
gof = struct( 'sse', cell( 4, 1 ), ...
    'rsquare', [], 'dfe', [], 'adjrsquare', [], 'rmse', [] );
maxmu=max(mu);
minmu=min(mu);
txtwidth=(maxmu-minmu)/4;
txtposy=maxmu-txtwidth;

minPf=min(Pf);
maxPf=max(Pf);
txtposx=(minPf+maxPf)/2;
% iternum0=200;
% stopeps=0.001;
optspow1 = fitoptions( 'Method', 'NonlinearLeastSquares' );
optspow1.Algorithm = 'Levenberg-Marquardt';
optspow1.Display = 'Off';



%% Fit: 'ns1_power2'.
[xData, yData] = prepareCurveData( Pf, mu );

% Set up fittype and options.
ft = fittype( 'power2' );
opts = fitoptions( 'Method', 'NonlinearLeastSquares' );
opts.Algorithm = 'Levenberg-Marquardt';

opts.Display = 'Off';
% opts.StartPoint = [0.0106945315846686 -0.975627188324542 0.030171985325082];

% Fit model to data.
% [fitresult{2}, gof(2)] = fit( xData, yData, ft, opts );

[fitresult{2}, gof(2)] = fit( xData, yData, ft, opts );
ci=predint(fitresult{2},xData);
[fitresult{2},gof(2)]=fit(xData,ci(:,1),ft,opts);


subplot(2,1,1);
% Plot fit with data.
% figure( 'Name', 'ns1_power2' );
h = plot( fitresult{2}, xData, yData);%,'predobs', 0.9 );
% paranames=coeffnames(fitresult{2});
paravalues=coeffvalues(fitresult{2});
parabnds=confint(fitresult{2});
ylim([0.6,1]);
legend( h, '\mu_m_i_n vs. P_f_,_I_L_S', 'f_\mu(x)=p_1*x^p^2+p_3', 'Location', 'SouthWest' );
title('f_\mu(x)=p_1*x^p^2+p_3');
xlabel ('P_f_,_I_L_S');
ylabel ('\mu_m_i_n');

str1=strcat('p_1= ',num2str(paravalues(1)));
str2=strcat('p_2= ',num2str(paravalues(2)));
str3=strcat('p_3= ',num2str(paravalues(3)));

% str4=strcat('SSE: ',num2str(gof(2).sse));
% str5=strcat('ad R^2: ',num2str(gof(2).adjrsquare));
str4=strcat('RMSE: ',num2str(gof(2).rmse));
% str7=strcat('ReRMSE: ',num2str(RealRMSE));

text(txtposx,txtposy,str1,'FontSize',fontsize); 
text(txtposx,txtposy-txtwidth,str2,'FontSize',fontsize);
text(txtposx,txtposy-2*txtwidth,str3,'FontSize',fontsize);
text(txtposx,txtposy-3*txtwidth,str4,'FontSize',fontsize);
% text(txtposx,txtposy-4*txtwidth,str5);
% text(txtposx,txtposy-5*txtwidth,str6);
% text(txtposx,txtposy-6*txtwidth,str7);

set(gca,'FontSize',fontsize);
grid on

subplot(2,1,2);
plot(fitresult{2},xData,yData,'Residuals');
ylim([-0.02,0.08]);
xlabel ('P_f_,_I_L_S');
ylabel('$\mu_{min}-\hat{\mu}_{min}$','Interpreter','LaTex')
legend('Residuals','Zero line');
